import React, { useState, useRef } from "react";
import * as cocoSsd from "@tensorflow-models/coco-ssd";
import "@tensorflow/tfjs";

const ImageUpload = () => {
  const [file, setFile] = useState(null);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const imageRef = useRef();
  const canvasRef = useRef();

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const img = URL.createObjectURL(e.target.files[0]);
      setFile(img);
      setResults([]);
    }
  };

  const analyzeImage = async () => {
    if (!file) return alert("Please upload an image first!");
    setLoading(true);

    const imgElement = imageRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const model = await cocoSsd.load();
    const predictions = await model.detect(imgElement);

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.drawImage(imgElement, 0, 0, canvas.width, canvas.height);

    predictions.forEach((p) => {
      const [x, y, width, height] = p.bbox;
      ctx.strokeStyle = "#00ff00";
      ctx.lineWidth = 2;
      ctx.strokeRect(x, y, width, height);
      ctx.font = "16px Arial";
      ctx.fillStyle = "#00ff00";
      ctx.fillText(`${p.class} (${(p.score * 100).toFixed(1)}%)`, x, y > 10 ? y - 5 : 10);
    });

    setResults(predictions);
    setLoading(false);
  };

  return (
    <div style={{ textAlign: "center" }}>
      <input type="file" accept="image/*" onChange={handleImageChange} />
      <br />
      {file && (
        <div style={{ position: "relative", display: "inline-block", marginTop: "20px" }}>
          <img
            src={file}
            alt="upload"
            ref={imageRef}
            style={{ width: "500px", display: "block" }}
            onLoad={(e) => {
              const canvas = canvasRef.current;
              canvas.width = e.target.width;
              canvas.height = e.target.height;
            }}
          />
          <canvas
            ref={canvasRef}
            style={{
              position: "absolute",
              left: 0,
              top: 0,
            }}
          />
        </div>
      )}
      <br />
      <button onClick={analyzeImage} disabled={loading}>
        {loading ? "Analyzing..." : "Analyze PPE"}
      </button>

      {results.length > 0 && (
        <div style={{ marginTop: "20px" }}>
          <h3>Detected Items:</h3>
          <ul style={{ listStyle: "none" }}>
            {results.map((r, i) => (
              <li key={i}>
                {r.class} â€” Confidence: {(r.score * 100).toFixed(1)}%
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ImageUpload;
